changeSet.sql:
/*用户表*/
drop table if exists `users`;
create table `users`
(
    id   int(11) primary key auto_increment,
    name varchar(64),
    age  int(3)
);
/*订单表*/
drop table if exists `order`;
create table `order`
(
    id   int(11) primary key auto_increment,
    code varchar(64),
    productId varchar(64),
    amount int(11),
    remark  varchar(512)
);
